#Sets are boring
DNA = set('')
print(DNA)


# Let's try something more fun with a dictionary
# Figuring out how many unique keys we need
UniqueNewDNA = set('')
print(UniqueNewDNA)

#Making a string that contains our sequence
newDNA = ''


# Making the string iterable
newDNAit = iter(newDNA)
countA = 0
countT = 0
countG = 0
countC = 0
# Counting shit in the string
for nt in newDNAit:
	if nt == 'A':
	 	countA += 1
	if nt == 'T':
		countT += 1
	if nt == 'G':
		countG += 1
	if nt == 'C':
		countC += 1

# Making a dictionary that contains the unique keys and the counts
DNAcounts = {
'A' : countA,
'T' : countT,
'G' : countG,
'C' : countC
}

print(DNAcounts)

#getting GC content

gcContent = ( DNAcounts['G'] + DNAcounts['C'])/len(newDNA)
print('GC content =',gcContent)
